/// <reference types="@sveltejs/kit" />
/// <reference types="svelte" />
/// <reference types="vite/client" />

declare namespace App {
	interface Locals {
		iOS: boolean;
		Android: boolean;
	}

	interface Platform {}

	interface Session {
		iOS?: boolean;
		Android?: boolean;
	}

	interface Stuff {
		path?: string;
		page?: string;
	}
}

/// <reference types="@sveltejs/kit" />
/// <reference types="svelte" />
/// <reference types="vite/client" />
/// <reference types="vite-plugin-comlink/client" />

declare namespace svelte.JSX {
	interface HTMLAttributes<T> {
		onclick_outside?: () => void;
		ondragmove?: (e: any) => void;
		onenterViewport?: () => void;
		onlosefocus?: () => void;
		onpan?: (event: any) => void;
		onpressing?: (event: any) => void;
		onpanend?: (event: any) => void;
	}
}
interface Array<T> {
	at(index: number): T | undefined;
}

@media (prefers-reduced-motion: reduce) {
	*,
	::before,
	::after {
		-webkit-animation-delay: -1ms !important;
		animation-delay: -1ms !important;
		-webkit-animation-duration: 1ms !important;
		animation-duration: 1ms !important;
		-webkit-animation-iteration-count: 1 !important;
		animation-iteration-count: 1 !important;
		background-attachment: initial !important;
		scroll-behavior: auto !important;
		transition-delay: 0s !important;
		transition-duration: 0s !important;
	}
}

.sr-only:not(:focus):not(:active) {
	clip: rect(0 0 0 0);
	clip-path: inset(50%);
	height: 1px;
	overflow: hidden;
	position: absolute;
	white-space: nowrap;
	width: 1px;
}

@forward "./reset";
@forward "./variables";
@forward "./css-variables";
@forward "./colors";
@forward "./layout";
@forward "./forms";
@forward "./media";
@forward "./typography";
@forward "./theme";
@forward "./accessibility";

@use "./variables" as vars;
:root {
	--base-spacing: 0.75rem;
	--base-font-size: 0.75rem;
	--base-font-family: system-ui, -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Oxygen, Ubuntu, Cantarell,
		"Open Sans", "Helvetica Neue", sans-serif;

	--secondary-font-family: "CommissionerVariable", sans-serif;
	--secondary-font-color: rgb(175, 175, 175);

	--color-dark: hsl(0deg 0% 98%);
	--color-light: #111113;
	--color-med: #202024;

	--sm-spacing: #{vars.$sm-spacing};
	--md-spacing: #{vars.$md-spacing};
	--lg-spacing: #{vars.$lg-spacing};
	--xl-spacing: #{vars.$xl-spacing};

	--base-radius: #{vars.$base-radius};
	--xs-radius: #{vars.$xs-radius};
	--sm-radius: #{vars.$sm-radius};
	--md-radius: #{vars.$md-radius};
	--lg-radius: #{vars.$lg-radius};
	--xl-radius: #{vars.$xl-radius};

	--max-width: 85.375rem;
}

form {
	&.inline {
		display: inline-flex;
		align-items: center;
		height: auto;
		min-height: 5ch;
		max-height: 30ch;
		margin-left: auto;
	}
}

label {
	font-weight: 400;
	font-family: system-ui, -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Oxygen, Ubuntu, Cantarell, "Open Sans",
		"Helvetica Neue", sans-serif;
	font-variant-caps: all-small-caps;
	letter-spacing: -0.02em;
}

[type="checkbox"],
[type="radio"] {
	display: inline;
	font-family: initial;
	margin-right: calc(var(--sm-spacing) * 0.5);
}

[type="file"] {
	margin-bottom: var(--sm-spacing);
	width: 100%;
}

input:not([type="range"]):not([type="checkbox"]):not([type="radio"]) {
	appearance: none;
	background: transparent;
	border: none;
	outline: none;
	color: inherit;
	box-sizing: border-box;
	padding: 0.6666666667em;
	font-family: var(--base-font-family);
	letter-spacing: -0.02em;
	font-size: 1em;
}

input:not([type="range"]):not([type="checkbox"]):not([type="search"]) {
	border: 0.0625em solid rgba(170, 170, 170, 0.219);
	font-family: inherit;
	width: inherit;
	border-radius: inherit;
}

input[type="search"]::-webkit-search-decoration,
input[type="search"]::-webkit-search-cancel-button,
input[type="search"]::-webkit-search-results-button,
input[type="search"]::-webkit-search-results-decoration {
	filter: brightness(10);
}

.input {
	position: relative;

	height: auto;
	min-height: 5ch;
	max-height: 30ch;
	min-width: 15ch;
	max-width: 32ch;
	width: 100%;

	transition: 100ms cubic-bezier(0.47, 0.25, 0.745, 0.715);
	transition-property: border-color, filter, color;

	border: 0.0625em solid hsla(0, 0%, 66.7%, 0.219);
	border-radius: var(--sm-radius);
	box-shadow: 0 0 0.25em 0.02em hsla(0, 0%, 66.7%, 0) !important;

	background: var(--form-bg);

	&.block {
		max-width: 100%;
	}
	::placeholder {
		font-weight: 400;
		opacity: 0.7;
	}

	@media screen and (hover: hover) {
		&:hover {
			filter: brightness(1.07);
			border-color: rgba(114, 114, 114, 0.663);
		}
	}
	&:hover:active,
	&:focus-within {
		border-color: rgba(158, 158, 158, 0.658);
		filter: brightness(1.15);
	}
	&:has(input[type="search"]) {
		min-width: 15ch;
		max-width: 80ch;
	}
	&:has(input[type="search"]) {
		display: grid;
		grid-template-columns: 1fr 2.75rem;
		grid-template-areas: "start end";
	}
}

input[type="search"] {
	-webkit-appearance: none;
	background: #0000;
	border: none;
	appearance: none;
	width: inherit;
}

.searchBtn {
	pointer-events: all;
	cursor: pointer;
	z-index: 5;
	display: inline-grid;
	place-items: center;
	background-color: #0000;

	z-index: 10;
	color: inherit;
	transition: 100ms cubic-bezier(0.47, 0.25, 0.745, 0.715) background-color;

	grid-area: end;
	// background-clip: content-box;
	border-radius: inherit;
	border-top-left-radius: 0;
	border-bottom-left-radius: 0;
	&:hover {
		background-color: var(--form-contrast-bg) !important;
	}
}

.no-scroll {
	overflow: hidden !important;
}
.hidden {
	display: none !important;
	visibility: hidden !important;
}

@use "../utility/mixins/mixins" as *;
@use "./variables" as v;
@use "./css-variables" as *;
html {
	box-sizing: border-box;
	scroll-behavior: smooth;
	-webkit-overflow-scrolling: touch;
	font-size: 12px;
	overscroll-behavior: contain;
}

*,
*::before,
*::after {
	box-sizing: inherit;
}

html,
body {
	background-color: #010102;
	color: var(--color-dark);
	line-height: 1.75;
	position: fixed;
	min-height: 100vh;
	min-width: 100vw;
	inset: 0;
	overscroll-behavior: contain;
	max-height: 100%;
	overflow: hidden;
	margin: 0;
}

:root {
	font-family: system-ui, -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Oxygen, Ubuntu, Cantarell, "Open Sans",
		"Helvetica Neue", sans-serif;
	word-wrap: break-word;
	-webkit-text-size-adjust: 100%;
	--base-font-size: 0.75rem;

	/* ===== Scrollbar CSS ===== */

	* {
		scrollbar-gutter: 0.833333rem;
		scrollbar-width: thin;
		scrollbar-color: #c7c7c7 #5e5e5e2f;
	}

	::-webkit-scrollbar {
		width: 0.916667rem;
		height: 0.916667rem;
	}

	::-webkit-scrollbar-track {
		background: #74747e21;
		border-radius: 0.625rem;
		height: 0.916667rem;
		width: 100%;
		background-clip: content-box;
		border-radius: 0.833333rem;
		border: transparent solid 0.0983333rem;
	}

	::-webkit-scrollbar-thumb {
		background-color: #e0e0e0a8;
		border-radius: 0.5625rem;
		width: 0.5625rem;
		scrollbar-width: 0.625rem;
		border: 0.25rem solid #b8b8b800;
		background-clip: content-box;
	}

	-webkit-touch-callout: none;
	-webkit-user-select: none;
	-moz-user-select: none;
	-ms-user-select: none;
	user-select: none;
	-webkit-tap-highlight-color: transparent !important;
	-webkit-font-smoothing: -subpixel-antialiased;
	--player-bar-height: clamp(5rem, 5.6rem, 5.95rem);
	--top-bar-height: clamp(3.9rem, 4.175rem, 4.625rem);
	text-rendering: geometricPrecision;
	font-smooth: auto;
	@media screen and (min-width: 40em) and (max-width: 115.375em) {
		--top-bar-height: clamp(3.8rem, 4rem, 4.75rem);
		--base-font-size: 0.875rem;
	}
	@media screen and (min-width: 115.375em) and (max-width: 134em) {
		--top-bar-height: clamp(3.8rem, 4rem, 4.75rem);
		--base-font-size: 1rem;
	}
	@media screen and (min-width: 134.75em) {
		--top-bar-height: clamp(3.8rem, 4rem, 4.75rem);
		--base-font-size: 1.125rem;
	}
}
.tooltip {
	position: absolute;
	display: block;
	pointer-events: none;

	opacity: 0;
	will-change: opacity, transform;
	transition: opacity linear 100ms;

	&::before,
	&::after {
		transform: rotate(-135deg);
		width: 8px;
		height: 8px;
		bottom: 50%;
		right: 50%;
		content: "";
	}
	transform: translate(calc(var(--tt-pos-x)), var(--tt-pos-y));
	transform-origin: center;

	border: 1px solid #323232;
	box-shadow: 0px 0px 1px #323232;
	background: #121212;
	font-size: v.$size-1;
	font-family: system-ui, -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Oxygen, Ubuntu, Cantarell, "Open Sans",
		"Helvetica Neue", sans-serif;
	border-radius: 4px;
	color: #f1f1f1;
	padding: 0.3rem;
	font-weight: 500;
	min-width: 1rem;
	max-width: 20rem;
	text-transform: capitalize;
	width: -webkit-max-content;
	width: -moz-max-content;
	width: max-content;
	position: absolute;
	display: block;
	z-index: 500;
}

body {
	margin: 0;
	overflow: hidden;
	position: fixed;
}
html {
	font-size: var(--base-font-size);
}
#app {
	position: fixed;
	height: 100%;
	width: 100%;
	line-height: 1.3;
	inset: 0;

	max-width: 100%;
	overscroll-behavior: contain;
	contain: strict;
}
main {
	margin: 0 auto;
	position: relative;
	@include v.content-spacing($type: "padding");
	transform: translateZ(0px);
	overscroll-behavior: contain;
	@media screen and (min-width: 640px) {
		@include v.content-width();
	}
}
.backdrop {
	position: fixed;
	z-index: 50;
	inset: 0;
	overflow: hidden;
	background-color: #00000063;
	width: 100%;
	height: 100%;
	contain: paint;
}
.wrapper {
	overflow: hidden;
	overflow-y: auto;
	-webkit-overflow-scrolling: touch;
	display: block;
	touch-action: pan-y;
	overscroll-behavior: contain;
	position: absolute;
	inset: 0;
	isolation: isolate;
}
.app-content-m {
	margin-block-start: calc(var(--top-bar-height) + 0px);
	will-change: margin-block;
	transform: translateZ(0px);
	isolation: isolate;
	&[hasplayer="true"] {
		will-change: unset;
		margin-block-end: calc(var(--player-bar-height) + 0px);
	}
}

.container {
	max-width: 112rem;

	position: relative;
	display: flex;
	width: 100%;
	flex-direction: column;
	&.row {
		flex-direction: row;
	}
}

.resp-content-width {
	margin: 0 auto;
	@include v.content-width(!important);
}
.no-scroll {
	overflow: hidden;
	overflow-y: hidden;
}

.hidden {
	display: none;
}

.footer-container {
	width: 100%;
	max-width: 100%;
	isolation: isolate;
	z-index: 25;
	@media screen and (min-width: 720px) {
		z-index: 152;
	}
}

.footer-container {
	bottom: 0;
	display: block;
	width: 100%;
	position: fixed;
	contain: layout;
	height: var(--player-bar-height);
	min-width: 100%;
	display: flex;
	min-width: 100%;
}

:root .dark * {
	color: hsl(0, 0%, 95%);
}
.player {
	color: #f3f3f3;
}

img,
picture {
	margin: 0;
	border-radius: var(--xs-radius);
	max-width: 100%;

	-webkit-user-drag: none;
	-khtml-user-drag: none;
	-moz-user-drag: none;
	-o-user-drag: none;
	user-drag: none;

	-webkit-touch-callout: none;
	-webkit-text-size-adjust: none;
	-webkit-user-select: none;
	user-select: none;
}

img::before,
video::before {
	display: block;
	content: "";
	position: absolute;
	padding-top: calc(100% * 2 / 3);
}

/* Document
 * ========================================================================== */

/**
 * Add border box sizing in all browsers (opinionated).
 */

*,
::before,
::after {
	box-sizing: border-box;
}

/**
	 * 1. Add text decoration inheritance in all browsers (opinionated).
	 * 2. Add vertical alignment inheritance in all browsers (opinionated).
	 */

::before,
::after {
	text-decoration: inherit;
	vertical-align: inherit;
}

/**
	 * 1. Use the default cursor in all browsers (opinionated).
	 * 2. Change the line height in all browsers (opinionated).
	 * 3. Use a 4-space tab width in all browsers (opinionated).
	 * 4. Remove the grey highlight on links in iOS (opinionated).
	 * 5. Prevent adjustments of font size after orientation changes in
	 *    IE on Windows Phone and in iOS.
	 * 6. Breaks words to prevent overflow in all browsers (opinionated).
	 */

html {
	cursor: default;
	line-height: 1.5;
	-moz-tab-size: 4;
	tab-size: 4;
	-webkit-tap-highlight-color: transparent;
	-ms-text-size-adjust: 100%;
	-webkit-text-size-adjust: 100%;
	word-break: break-word;
}

/* Sections
	 * ========================================================================== */

/**
	 * Remove the margin in all browsers (opinionated).
	 */

