	display: block;
}

/**
	 * Remove the list style on navigation lists in all browsers (opinionated).
	 */

nav ol,
nav ul {
	list-style: none;
	padding: 0;
}

/**
	 * 1. Correct the inheritance and scaling of font size in all browsers.
	 * 2. Correct the odd `em` font sizing in all browsers.
	 */

