video {
	display: inline-block;
}

/**
	 * Add the correct display in iOS 4-7.
	 */

audio:not([controls]) {
	display: none;
	height: 0;
}

/**
	 * Remove the border on iframes in all browsers (opinionated).
	 */

iframe {
	border-style: none;
}

/**
	 * Remove the border on images within links in IE 10-.
	 */

