	 * ========================================================================== */

/*
	 * 1. Remove the tapping delay in IE 10.
	 * 2. Remove the tapping delay on clickable elements
				in all browsers (opinionated).
	 */

a,
area,
button,
input,
label,
select,
summary,
textarea,
[tabindex] {
	-ms-touch-action: manipulation;
	touch-action: manipulation;
}

/**
	 * Add the correct display in IE 10-.
	 */

[hidden] {
	display: none;
}

/* Accessibility
	 * ========================================================================== */

/**
	 * Change the cursor on busy elements in all browsers (opinionated).
	 */

[aria-busy="true"] {
	cursor: progress;
}

/*
	 * Change the cursor on control elements in all browsers (opinionated).
	 */

[aria-controls] {
	cursor: pointer;
}

/*
	 * Change the cursor on disabled, not-editable, or otherwise
	 * inoperable elements in all browsers (opinionated).
	 */

[aria-disabled="true"],
[disabled] {
	cursor: not-allowed;
}

/*
	 * Change the display on visually hidden accessible elements
	 * in all browsers (opinionated).
	 */

[aria-hidden="false"][hidden] {
	display: initial;
}

[aria-hidden="false"][hidden]:not(:focus) {
	clip: rect(0, 0, 0, 0);
	position: absolute;
}

$sections: (
	bottom: (
		footer-container,
		player,
	),
	top: (
		nav,
	),
	forms: (
		select,
		input,
		option,
		suggestions,
	),
);

$themes: (
	YTM: (
		"bottom": linear-gradient(0deg, #121018 28%, #18171e 100%),
		"base": #010102,
		"top": #010102,
		"form": #1b1922,
		"side": #0e0c13,
	),
	Dark: (
		"bottom": linear-gradient(0deg, #121216 28%, #1a1a20 100%),
		"base": #010102,
		"top": linear-gradient(0deg, #101014 28%, #17171d 100%),
		"form": #1c1f20,
		"side": #15191d,
	),
	Dim: (
		"bottom": #141820,
		"base": #010102,
		"top": #141820,
		"form": #1c1f20,
		"side": #0f1116,
	),
	Midnight: (
		"base": #010102,
		"top": hsl(267, 14%, 6%),
		"side": #15131f,
		"bottom": linear-gradient(0deg, #12131c 32%, #191a26 100%),
		"form": #1b1922,
	),
);
$theme-name: (Dark, Dim, Midnight, YTM);
$section-names: (base, top, side, bottom, form);

:root {
	--mobile-popper-bg: unset;
}
html {
	@each $section in $section-names {
		@each $theme in $theme-name {
			--#{$theme}-#{$section}: #{map-get($themes, $theme, $section)};
		}
	}
	@each $theme in $theme-name {
		&.#{$theme} {
			--mobile-popper-bg: #{map-get($themes, $theme, "side")};
			--form-bg: #{map-get($themes, $theme, "form")};
			--form-contrast-bg: #{scale-color(
					map-get($themes, $theme, "form"),
					$lightness: 33%,
					$saturation: -100%,
					$alpha: -85%
				)};
			--disabled-form-bg: #{scale-color(map-get($themes, $theme, "form"), $lightness: 17%, $alpha: -25%)};
			--disabled-form-fg: #{scale-color(map-get($themes, $theme, "form"), $lightness: 60%)};
			--bottom-bg: #{map-get($themes, $theme, "bottom")};
			--bottom-bg-light: #{scale-color(map-get($themes, $theme, "base"), $lightness: 8%, $saturation: -66%)};
			--top-bg: #{map-get($themes, $theme, "top")};
			--base-bg: #{map-get($themes, $theme, "base")};
			background: var(--base-bg);

			@each $section, $elm in $sections {
				.#{$elm} {
					background: #{var(--#{$section}-bg)};
				}
			}
		}
	}
}

@use "./variables" as v;

h1,
h2,
h3,
h4,
h5,
h6,
.h1,
.h2,
.h3,
.h4,
.h5,
.h6,
.subheading {
	font-family: "CommissionerVariable", sans-serif;

	line-height: 1.2;
	font-weight: 500;
	margin: 0.5em 0 1.38rem;
}

h1,
.h1 {
	font-size: 2.488rem;
	font-weight: 700;
	letter-spacing: -0.03em;
}

h2,
.h2 {
	font-size: 2.074rem;
	font-weight: 700;
}

h3,
.h3 {
	font-size: 1.728em;
}

h4,
.h4 {
	font-size: 1.44em;
}

h5,
.h5 {
	font-size: 1.2em;
}

h6,
.h6 {
	font-size: 1em;
}

p {
	margin: 0 0 v.$sm-spacing;
	font-size: 1em;
}

a.no-style {
	text-decoration: none;
	&:focus,
	&:focus-visible,
	&:focus-within {
		outline: none;
	}
}

a:not(.no-style),
.link:not(.no-style) {
	$link-color: rgb(245, 245, 245);
	color: rgb(245, 245, 245);

	text-decoration: none;
	transition: color 0.2s;
	display: inline;
	&.link {
		font-weight: 500;
	}
	&:active,
	&:focus,
	&:hover {
		-webkit-text-decoration: underline 0.001em solid;
		text-decoration: underline 0.001em solid;
		text-underline-offset: 0.001em;
		color: darken($link-color, 15%);
		outline: none;
	}
	&:hover {
		cursor: pointer;
	}
}

.explicit {
	text-shadow: none;
	/* max-height: 1em; */
	min-width: 1em;
	min-height: 1em;
	/* width: 1em; */
	position: relative;
	place-content: center;
	display: inline-flex;
	// contain: paint;
	font-size: 0.5em;
	text-rendering: optimizeLegibility;
	&::before {
		content: "E";
		position: absolute;

		z-index: 5;

		place-self: center;
		// inset:0;
		font-weight: 700;
		aspect-ratio: 1/1;
		line-height: 1.1;
	}
	font-size: calc(1em * 0.25 + 4px);
	padding: 0.0875rem;
	aspect-ratio: 1/1;
	background: #ffffffbf;
	border-radius: clamp(calc(calc(0.25em * 0.5) + 0.1px), 16px * 0.0125, 4px);
	vertical-align: middle;
	font-family: -apple-system, BlinkMacSystemFont, Segoe UI, Roboto, Oxygen, Ubuntu, Cantarell, Open Sans, Helvetica Neue,
		sans-serif;

	color: rgb(8, 8, 8) !important;
}

a small {
	$color: rgb(175, 175, 175);
	font-size: 0.95rem;
	font-weight: 700;
	font-variant-caps: all-petite-caps;
	letter-spacing: 0.05rem;
	transition: ease-in color 75ms;
	color: $color;
	&:hover {
		color: lighten($color, 30%);
		text-decoration: underline 0.001rem solid;
		text-underline-offset: 0.001rem;
	}
}

.secondary {
	color: rgba(255, 255, 255, 0.7) !important;
	font-weight: 400;
}

@use "sass:math" as m;

$size-base: 0.75rem;
$size-1: 1rem;
$size-2: 1.2rem;
$size-3: 1.44rem;
$size-4: 1.728rem;
$size-5: 2.074rem;

$base-spacing: 0.75em;
$sm-spacing: m.div($base-spacing, 2.5);
$md-spacing: $base-spacing;
$lg-spacing: calc($base-spacing * 2);
$xl-spacing: calc($base-spacing * 1.25);

$base-radius: 0.35em;
$xs-radius: m.div($base-radius, 0.75);
$sm-radius: m.div($base-radius, 0.675);
$md-radius: m.div($base-radius, 0.575);
$lg-radius: m.div($base-radius, 0.5);
$xl-radius: m.div($base-radius, 0.3125);

$secondary-font-family: "CommissionerVariable", sans-serif;

$line-height: 1.5;

$z-index: (
	"z-250n": -250,
	"z-125n": -125,
	"z-1n": -1,
	"z-0": 0,
	"z-125p": 125,
	"z-250p": 250,
	"z-500p": 500,
);

$content-width-mobile: calc(100vw - 2.25em);

$content-width-md: calc(100vw - 0.5625rem - 14rem);
$content-width-lg: 82rem;
$content-width-xl: 87.5rem;

@mixin content-spacing($type: "padding") {
	#{$type}: 1.25em 0 5vh;
	#{$type}-bottom: 3.3125vh !important;
	@media only screen and (min-width: 640px) and (max-width: 1079.9px) {
		#{$type}: 3vh 1rem 0 2rem;
		#{$type}-bottom: 0.8rem;
	}
	@media only screen and (min-width: 1080px) and (max-width: 1366px) {
		#{$type}: 4vh 2rem 0 2rem;
		#{$type}-bottom: 0.8rem;
	}
	@media only screen and (min-width: 1367px) and (max-width: 1600px) {
		#{$type}: 5vh 2rem 0 2rem;
	}
	@media only screen and (min-width: 1601px) {
		#{$type}: 3vh 2rem 0 2rem;
	}
}

@mixin content-width($important: "") {
	max-width: $content-width-mobile #{$important};
	@media only screen and (min-width: 1080px) and (max-width: 1366px) {
		max-width: $content-width-md #{$important};
	}
	@media only screen and (min-width: 1367px) and (max-width: 1600px) {
		max-width: $content-width-lg #{$important};
	}
	@media only screen and (min-width: 1601px) {
		max-width: $content-width-xl #{$important};
	}
}

@forward "./base/base";
@forward "./modules/modules";
@forward "./utility/utility";

.alert {
	margin-bottom: 1.125rem;
}
.m-alert {
	position: relative;
	display: block;
	max-width: 85.735rem;
	padding: 1rem 2rem;
	border-radius: 0.8rem;
	width: calc(100% - 2rem);
	text-align: center;
	text-shadow: 0.1rem 0.05rem rgba(0, 0, 0, 0.685);
	color: #fff;
	background-color: #ce7c7c;
	&-error {
		@extend .m-alert;
		background-color: #b32f2a !important;
	}
	&-success {
		@extend .m-alert;
		background-color: #1d964b !important;
	}
}

@use "../base/variables" as v;
@use "sass:color";

$background: hsl(0deg 0% 98%);
$color: #0d0d0d;
$focus-background: #aaa;
$focus-color: #1c1c1c;
%active {
	color: $focus-color;

	border-color: $focus-color !important;

	outline: 0;
	text-decoration: none;
	cursor: pointer;
}
%button-base {
	display: inline-flex;
	color: #0f0f0f !important;
	background: $background;

	border: 0.15em solid hsl(0deg 0% 7%);
	border-radius: v.$base-radius;
	font-family: "CommissionerVariable", sans-serif;
	cursor: pointer;
	font-size: 1em;
	gap: 0.25em;
	font-weight: 550;
	text-transform: none;
	white-space: nowrap;
	cursor: pointer;
	-webkit-user-select: none;
	-moz-user-select: none;
	-ms-user-select: none;
	user-select: none;
	padding: 0.63125em 1.25em;
	text-transform: capitalize;
	font-size: 1em;
	justify-content: center;
	align-items: center;
	vertical-align: middle;
	transition: background linear 75ms, color linear 75ms;
	@media (hover: hover) {
		&:hover {
			color: #252525 !important;
			background: color.scale($focus-background, $lightness: 12%) !important;
			border-color: #202020 !important;
			box-shadow: -0.01em -0.01em 0.1em 0rem rgba(0, 0, 0, 0.904) inset;
			outline: 0;
			text-decoration: none;
		}
	}
	&:focus,
	&:focus-within,
	&:active,
	&.active {
		border-color: $focus-color !important;
		background: color.scale($focus-background, $lightness: 25%) !important;

		color: $focus-color;

		border-color: $focus-color !important;

		outline: 0;
		text-decoration: none;
		cursor: pointer;
	}
	&:disabled,
	.disabled {
		background: rgb(44, 44, 44) !important;
		color: #979797 !important;
		cursor: not-allowed;
		border-color: #444;
		&:hover {
			background: rgb(44, 44, 44) !important;
		}
	}
	&:hover:active,
	&:active {
		background: color.scale($focus-background, $lightness: 15%) !important;
	}
}

button:not(.icon-btn),
.button {
	@extend %button-base;
}

button.small {
	@extend %button-base;
	max-width: -webkit-fit-content;
	max-width: -moz-fit-content;
	max-width: fit-content;
}

button.has-icon {
	display: inline-flex;
	justify-items: center;
	justify-content: center;
	> .text {
		margin-left: 0.125rem;
	}
}

button.outlined {
	background: #0000 !important;
	border: $background 0.1em solid !important;
	color: $background !important;
	font-weight: 525;

	&:active {
		border: rgb(158, 158, 158) 0.1rem solid !important;
		background: rgba(255, 255, 255, 0.027) !important;
		box-shadow: 0 0 0.1em 0 inset black;
		color: rgb(236, 236, 236) !important;
	}

	.button-text {
		margin-left: 0.2rem;
		align-self: center;
		padding-bottom: 0.1rem;
	}
	@media (hover: hover) {
		&:hover {
			background: rgba(255, 255, 255, 0.034) !important;
			border-color: rgba(194, 170, 194, 0.667) !important;
			color: rgb(231, 221, 231) !important;
		}
	}
	&:focus,
	&:focus-within {
		background: rgba(255, 255, 255, 0.034) !important;
		border-color: rgba(194, 170, 194, 0.667) !important;
		color: rgb(231, 221, 231) !important;
	}
	&:active {
		background: rgba(255, 255, 255, 0.096) !important;

		border-color: rgba(255, 255, 255, 0.667) !important;
	}
}
button.icon-btn {
	display: flex;
	flex-wrap: nowrap;
	position: relative;
	cursor: pointer;

	place-items: center;
	place-content: center;

	width: 44px;
	height: 44px;
	border: none;
	background-color: hsl(0deg 0% 0% / 0%);
	color: var(--color-dark);
}

button.danger {
	border-color: #eb2019 !important;
	color: darken(#ff4949, 3%) !important;
	background: #5c191718 !important;
	&:hover {
		color: lighten(#ff4949, 3%) !important;
		border-color: #eb2019b2 !important;
		background: #5c19174d !important;
	}
	&:active {
		color: lighten(#fafafa, 10%) !important;
		border-color: #eb20195b !important;
		background: #c013136c !important;
	}
}

.dd-text {
	display: inline-block;
	margin-left: 0.5em;
	font-size: 0.95rem;
	white-space: nowrap;
	color: inherit;
}
.dd-button svg circle {
	/* box-shadow: 0 0 0.5rem 0.125rem filter: drop-shadow(0rem 0.02rem 0.5rem black) contrast(1);#000; */
	/* outline: solid #11111161 1px; */
	position: relative;
	&::before {
		content: "";
		position: absolute;
		width: 100%;
		height: 100%;
		inset: 0;
		-webkit-filter: drop-shadow(0rem 0rem 0.15rem hsl(0deg 0% 0% / 52%)) contrast(2);
		filter: drop-shadow(0rem 0rem 0.15rem hsl(0deg 0% 0% / 52%)) contrast(2);
	}
}

.dd-button svg {
	-webkit-filter: drop-shadow(-0rem -0rem 0.05rem hsl(0deg 0% 0% / 99%))
		drop-shadow(0rem 0rem 0.05rem hsl(0deg 0% 0% / 99%)) contrast(2);
	filter: drop-shadow(-0rem -0rem 0.05rem hsl(0deg 0% 0% / 99%)) drop-shadow(0rem 0rem 0.05rem hsl(0deg 0% 0% / 99%))
		contrast(2);
}

.dd-item {
	z-index: 5;
	display: flex;
	flex-direction: row;
	padding: 0.667em;
	align-items: center;
	cursor: pointer;
	margin: 0;
	font-size: inherit;

	background-color: #0000;
	color: #fff9f9;
	font-family: system-ui, -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Oxygen, Ubuntu, Cantarell, "Open Sans",
		"Helvetica Neue", sans-serif;
	position: relative;
	&:hover {
		color: #fcf6f6;
		background-color: #585869;
	}
	&:active {
		background-color: #a0a0b1;
	}
}

.dd-menu {
	position: absolute;
	display: flex;
	flex-direction: column;
	top: 0;
	left: 0;
	border-radius: var(--sm-radius);
	z-index: 200;

	transform: translate(0px, 0px);
	background: #333333;
	border: 1px #686868 solid;
	pointer-events: all;
}
.dd-player {
	position: absolute;
	top: 0%;

	isolation: isolate;
	left: 0%;
	border-radius: var(--lg-radius);
	z-index: 200;

	background: #333333;
	border: 1px #5f5f5f solid;
	pointer-events: all;

	flex-direction: column;
	flex-wrap: nowrap;
	.dd-item * {
		color: #fcfcfc !important;
	}
}

