input[type="range"] {
	-webkit-appearance: none;
	background: none;
	overflow: hidden;
	outline: none;
	background: hsl(0, 0%, 20%);
	border-radius: 1.666667rem;
	height: 0.6rem;
	cursor: pointer;
	transition-duration: 0.2s;

	&:hover {
		background: hsl(0, 0%, 32%);

		&::-webkit-slider-thumb {
			opacity: 1;
