.h5 {
	font-size: 1.2em;
}

h6,
.h6 {
	font-size: 1em;
}

p {
	margin: 0 0 var(--sm-spacing);
	font-size: 1em;
}
a.no-style {
	text-decoration: none;
	&:focus,
	&:focus-visible,
	&:focus-within {
		outline: none;
	}
}
a:not(.no-style),
.link:not(.no-style) {
	$link-color: rgb(245, 245, 245);
	color: rgb(245, 245, 245);

	text-decoration: none;
	transition: color 0.2s;
	display: inline;
	&.link {
		font-weight: 500;
	}
	&:active,
	&:focus,
	&:hover {
		-webkit-text-decoration: underline 0.001em solid;
		text-decoration: underline 0.001em solid;
		text-underline-offset: 0.001em;
		color: darken($link-color, 15%);
		outline: none;
	}
	&:hover {
		cursor: pointer;
	}
}

strong,
em,
small,
code,
hr {
	line-height: 1.25;
}

.secondary {
