}

@use "sass:math" as m;

$size-base: 0.75rem;
$size-1: 1rem;
$size-2: 1.2rem;
$size-3: 1.44rem;
$size-4: 1.728rem;
$size-5: 2.074rem;

$base-spacing: 0.75em;
$sm-spacing: m.div($base-spacing, 2.5);
$md-spacing: $base-spacing;
$lg-spacing: calc($base-spacing * 2);
$xl-spacing: calc($base-spacing * 1.25);

$base-radius: 0.5em;
$xs-radius: m.div($base-radius, 0.95);
$sm-radius: m.div($base-radius, 0.8);
$md-radius: m.div($base-radius, 0.625);
$lg-radius: m.div($base-radius, 0.5);
$xl-radius: m.div($base-radius, 0.3125);

$secondary-font-family: "CommissionerVariable", sans-serif;

$line-height: 1.5;

$content-width-mobile: calc(100vw - 2.25rem);

$content-width-md: calc(100vw - 0.5625rem - 14rem);
$content-width-lg: 82rem;
$content-width-xl: 87.5rem;

@mixin content-spacing($type: "padding") {
	#{$type}: 1em 0 5vh;
	#{$type}-bottom: 3.3125vh !important;
	@media only screen and (min-width: 640px) and (max-width: 1079.9px) {
		#{$type}: 3vh 1rem 0 2rem;
		#{$type}-bottom: 0.8rem;
	}
	@media only screen and (min-width: 1080px) and (max-width: 1366px) {
		#{$type}: 4vh 2rem 0 2rem;
		#{$type}-bottom: 0.8rem;
	}
	@media only screen and (min-width: 1367px) and (max-width: 1600px) {
		#{$type}: 5vh 2rem 0 2rem;
	}
	@media only screen and (min-width: 1601px) {
		#{$type}: 3vh 2rem 0 2rem;
	}
}

@mixin content-width($important: "") {
	max-width: $content-width-mobile #{$important};
	@media only screen and (min-width: 1080px) and (max-width: 1366px) {
		max-width: $content-width-md #{$important};
	}
	@media only screen and (min-width: 1367px) and (max-width: 1600px) {
		max-width: $content-width-lg #{$important};
	}
	@media only screen and (min-width: 1601px) {
		max-width: $content-width-xl #{$important};
	}
}

.nav-item {
	position: relative;
	display: block;
}
.nav-search {
	display: inline-flex;
	position: absolute;
	top: 0;
	left: 0;
	right: 0;
	width: 100%;
	padding: 0 2rem;
	z-index: 15;
	box-sizing: border-box;
	align-content: center;
	justify-content: center;
	flex-wrap: nowrap;
	flex-direction: row;
	max-width: 100%;
	align-items: center;

	background: inherit;

	&::before {
		backdrop-filter: brightness(1.1);
		pointer-events: none;
		box-shadow: 0 -0.5rem 1em 0.01rem #30303014 inset;
		position: absolute;
		top: 0;
		left: 0;
		right: 0;
		bottom: 0;
		width: 100%;
		content: "";
		z-index: -1;
	}
}

@media screen and (max-width: 719px) {
	.nav-search .x-button.icon-btn {
		display: none !important;
		visibility: none !important;
	}
}

.listButton {
	-webkit-text-size-adjust: 100%;
	align-self: center;
	cursor: pointer;
	display: inline-block;
	height: auto;
	margin-left: auto;
	margin-right: auto;
	max-height: 44pt;
	max-width: 44pt;
	margin: 10pt;
	width: auto;
}
.hidden {
	display: none !important;
}
.player-btn {
	-webkit-text-size-adjust: 100%;

	cursor: pointer;
	display: inline-flex;
	height: auto;
	max-height: 8em;
	max-width: 8em;
	margin: 0 0.1em;
	padding: 0.8em;

	position: relative;
	width: auto;
	isolation: isolate;

	&::before {
		position: absolute;
		content: "";
		z-index: -1;
		top: 0;
		right: 0;
		bottom: 0;
		left: 0;
		height: 100%;
		width: 100%;
		border-radius: 50%;
		opacity: 0;
		transition: opacity, background linear 200ms;

		background-position: center;
		padding: 8pt;
	}
	@media (hover: hover) {
		&:hover::before {
			background: #55555528 radial-gradient(circle, transparent 1%, #55555528 1%) center/15000%;
			opacity: 1;
		}
	}
	&:active::before {
		background-size: 100%;
		background-color: #7272723f;
		transition: background 0s;
	}
}

.controls {
	display: flex;
	flex: 1 auto;
	flex-direction: column;
	flex-wrap: nowrap;
	padding: inherit;
}
.player-container {
	align-items: center;

	bottom: 0;
	display: flex;
	height: 100%;
	flex-wrap: nowrap;

	flex-direction: column;
}

.buttons {
	display: flex;
	flex-direction: row;

	align-items: center;
}
.player {
	display: grid;
	grid-template-columns: 1.25fr 1.1fr 1.25fr;
	grid-template-areas: "n m r";
	padding-inline: 1em;
	max-width: 100%;
	min-width: calc(100% - 40px);
	width: 100%;
	align-items: center;
	@media screen and (max-width: 600px) {
		grid-template-columns: 3fr 1fr;
		grid-template-areas: "n r";
	}
}
input[type="range"] {
	-webkit-appearance: none;
	background: none;
	overflow: hidden;
	outline: none;
	background: #333333;
	border-radius: 1.666667rem;
	height: 0.6rem;
	cursor: pointer;
	transition-duration: 0.2s;

	&:hover {
		background: rgba(255, 255, 255, 0.295);

		&::-webkit-slider-thumb {
			opacity: 1;
		}
	}
	&::-webkit-slider-runnable-track {
		overflow: hidden;
	}
	&::-webkit-slider-thumb {
		-webkit-appearance: none;
		width: 0;
		height: 1rem;
		background: white;
		box-shadow: -100vw -100vh 0 100vw white;
		opacity: 0.7;
		transition-duration: 0.2s;
	}
}

