	),
	mt: margin-top,
	mr: margin-right,
	mb: margin-bottom,
	ml: margin-left,
) !default;

$sizes: (
	none: (
		0rem,
	),
	xs: (
		0.25rem,
	),
	sm: (
		0.5rem,
	),
	md: (
		1rem,
	),
	lg: (
		1.25rem,
	),
	xl: (
		1.5rem,
	),
) !default;

@at-root {
	@each $side, $side-list in $sides {
		@each $unit in $units {
			.#{$side}-#{index(($units), $unit)} {
				@each $attr in $side-list {
					#{$attr}: #{$unit} !important;
				}
			}
		}
	}
}

