export type EventType = string;
export type EventCallback<T = unknown> = (payload?: T) => void;
export type EventListeners<
	Events extends Record<string, unknown>,
	Name extends keyof Events = keyof Events & string,
> = Map<Name, EventCallback<Events[Name]>[]>;
type KeyOf<T> = keyof T & string;

export interface IEventEmitter<T> {
	dispatch<Name extends KeyOf<T> = KeyOf<T>>(type: Name, args?: T[Name]): void;
	off<Name extends KeyOf<T> = KeyOf<T>>(type: Name, cb: EventCallback<T[Name]>): void;
	on<Name extends KeyOf<T> = KeyOf<T>>(type: Name, cb: EventCallback<T[Name]>): void;
}

export function EventEmitterMixin<TBase extends new (...args: any[]) => {}, Events extends Record<string, any> = any>(
	Base: TBase,
) {
	return class EventEmitter extends Base implements IEventEmitter<Events> {
		__target: object;
		_eventQueue: WeakMap<object, EventListeners<any>>;

		constructor(...target: any[]) {
			super(...target);
			this.__target = target;
			this._eventQueue = new WeakMap<object, EventListeners<any>>();
		}

		dispatch<Name extends KeyOf<Events> = KeyOf<Events>>(type: Name, args?: Events[Name]): void {
			const queue = this._eventQueue.get(this.__target);

			if (!queue) {
				return;
			}

			const listeners = queue.get(type);

			if (!listeners) return;

			const length = listeners.length;
			let idx = -1;

			for (; ++idx < length; ) {
				listeners[idx](args);
			}
		}

		public dispose(): void {
			if (this._eventQueue !== null) this._eventQueue.delete(this.__target);
			this._eventQueue = null;
		}

		public off<Name extends KeyOf<Events> = KeyOf<Events>>(type: Name, cb: EventCallback<Events[Name]>): void {
			const listeners = this._eventQueue.get(this.__target);
			if (!listeners) return;
			if (type === undefined) {
				listeners.clear();
				// console.log(this._eventQueue, listeners);
				return;
			}
			if (cb === undefined) {
				listeners.delete(type);
				// console.log(this._eventQueue, listeners);
				return;
			}
		}

		public on<Name extends KeyOf<Events> = KeyOf<Events>>(type: Name, cb: EventCallback<Events[Name]>): void {
			if (!this.__target) return;
			const queue = this._eventQueue.get(this.__target) ?? (new Map() as EventListeners<any>);

			const listeners = queue.get(type) ?? [];
			queue.set(type, listeners.concat(cb));

			this._eventQueue.set(this.__target, queue);
		}

		public once<Name extends KeyOf<Events> = KeyOf<Events>>(name: Name, cb: EventCallback<Events[Name]>) {
			if (!this.__target) return;

			const doOnce = (data: Events[Name]) => {
				this.off(name, doOnce);
				cb(data);
			};

			this.on(name, doOnce);
		}
	};
}
export const EventEmitter = EventEmitterMixin(class P {});

export const Messenger = new EventEmitter({});

export const messenger = (function () {
	return {
		send(to: string, data: JSON) {
			Messenger.dispatch(to, data);
		},
		listen(to: string, cb: EventCallback) {
			Messenger.on(to, cb);
		},
		dispose() {
			Messenger.dispose();
		},
		off(name: string, cb?: EventCallback) {
			Messenger.off(name, cb);
		},
	};
})();

export type Releaser = () => void;
export type Callback<T> = () => Promise<T> | T;

export interface ISemaphoreQueueEntry {
	resolve(result: [number, Releaser]): void;
	reject(err: unknown): void;
}

export interface ISemaphore {
	isLocked(): boolean;
	acquire(value: number): Promise<[number, Releaser]>;

	waitForUnlock(value?: number): Promise<void>;

	setValue(value: number): void;
	release(value?: number): void;
	getValue(): number;

	cancel(): void;
}

export class Semaphore implements ISemaphore {
	#queue: ISemaphoreQueueEntry[][] = [];
	#waiting: (() => void)[][] = [];
	#value;
	constructor(weight = 3) {
		this.#value = weight;
	}
	isLocked(): boolean {
		return this.#value <= 0;
	}
	async do<T>(callback: Callback<T>): Promise<T> {
		const [_, release] = await this.acquire();

		try {
			return await callback();
		} finally {
			queueMicrotask(release);
		}
	}
	acquire(value = 1): Promise<[number, Releaser]> {
		if (value <= 0) throw new Error(`Value must be greater than 0. Received: #{value}`);

		return new Promise((resolve, reject) => {
			if (!this.#queue[value - 1]) {
				this.#queue[value - 1] = [];
			}
			this.#queue[value - 1].push({ resolve, reject });
			this.#dispatch();
		});
	}
	waitForUnlock(value: number | undefined = 1): Promise<void> {
		if (value <= 0) throw new Error(`Value must be greater than 0. Received: #{value}`);

		return new Promise((resolve, reject) => {
			if (!this.#waiting[value - 1]) {
				this.#waiting[value - 1] = [];
			}
			this.#waiting[value - 1].push(resolve);
			queueMicrotask(this.#dispatch);
		});
	}
	setValue(value: number): void {
		this.#value = value;
		this.#dispatch();
	}
	release(value?: number | undefined): void {
		this.#value += value!;
		this.#dispatch();
	}
	getValue(): number {
		return this.#value;
	}
	cancel(): void {
		const length = this.#queue.length;
		let idx = -1;
		while (++idx < length) {
			const queue = this.#queue[idx];
			const len = queue.length;
			let pos = -1;
			while (++pos < len) {
				queue[pos].reject(null);
			}
		}
		this.#queue = [];
	}

	#dispatch(): void {
		for (let weight = this.#value; weight > 0; weight--) {
			const queueEntry = this.#queue[weight - 1]?.shift();
			if (!queueEntry) continue;

			const previousValue = this.#value;
			const previousWeight = weight;

			this.#value -= weight;
			weight = this.#value + 1;

			queueEntry.resolve([previousValue, this.#newReleaser(previousWeight)]);
		}

		this.#drainUnlockWaiters();
	}

	#newReleaser(weight: number): () => void {
		let called = false;

		return () => {
			if (called) return;
			called = true;

			this.release(weight);
		};
	}

	#drainUnlockWaiters(): void {
		for (let weight = this.#value; weight > 0; weight--) {
			if (!this.#waiting[weight - 1]) continue;

			this.#waiting[weight - 1].forEach((waiter) => waiter());
			this.#waiting[weight - 1] = [];
		}
	}
}

export class Mutex extends Semaphore {
	constructor() {
		super(1);
	}
}

import { reduce } from "./collections";

export interface XMLNode {
	type: "element" | "declaration";
	name?: string;
	attrs?: Record<string, string | boolean | number>;
	nodes?: Array<XMLNode>;
	text?: string;
}

export interface XMLRoot {
	declaration: XMLNode;
	nodes?: Array<XMLNode>;
}
function buildAttrs(attributes: XMLNode["attrs"]) {
	let output = "";
	for (const attr in attributes) {
		output += ` ${attr}="${`${attributes[attr]}`.toString().replace('"', '\\"')}"`;
	}
	return output;
}

function buildElement(node: XMLNode): string {
	let element = "<";

	if (node.name) element += node.name;
	if (node.attrs) element += buildAttrs(node.attrs);
	element += node.nodes || node.text ? ">" : "/>";
	if (node.text)
		element += `${node.text}`.replace(/&amp;/g, "&").replace(/&/g, "&amp;").replace(/</g, "&lt;").replace(/>/g, "&gt;"); // desanitize to avoid double sanitization;
	if (node.nodes) element += buildElements(node.nodes);
	if (node.nodes || node.text) element += `</${node.name}>`;
	return element;
}
function buildDeclaration(node: XMLNode, depth = 0) {
	return `<?xml${buildAttrs(node["attrs"])}?>`;
}
function buildElements(nodes: XMLNode[]) {
	const elements = reduce(
		nodes,
		(output, node) => {
			switch (node.type) {
				case "element":
					return output + buildElement(node);
				case "declaration":
					return output + buildDeclaration(node);
			}
		},
		"",
	);
	return elements;
}

export function buildXML(input: XMLRoot) {
	let output = "";
	const depth = 0;
	if (input["declaration"]) {
		output += buildDeclaration(input["declaration"], depth);
	}
	if (input["nodes"]) {
		output += buildElements(input["nodes"]);
	}
	console.log(output);
	return output;
}

