		contents: {
			singleColumnBrowseResultsRenderer: {
				tabs: [{ tabRenderer: { content: { sectionListRenderer: { contents = [] } = {} } = {} } = {} } = {}] = [],
			} = {},
		} = {},
	} = await data;
	let type: string;

	slug.includes("videos") ? (type = "videos") : (type = "albums");
	let carousels = [];
	let grids = [];
	let sections = [];
